import React from "react";
import Link from 'next/link';

const StoryList = ({stories}) => (
    <div className="story-list">
        {stories.map(story =>
            <div className="story" key={story.id}>
                <h2 className="story-title">
                    <a href={story.url}>{story.title}</a>
                </h2>
                <div className="story-details">
                    <span>
                        {story.points || 0} points
                    </span>
                    <Link href={`/story/${story.id}`}><a>{story.comments_count} comments</a></Link>
                </div>
            </div>
        )}
        <style jsx>
            {
                `
              .story-list {
                padding: 0 1em;
              }
              .story {
                padding: 0 1em;
              }
              .story-details {
                font-size: 0.8rem;
                font-weight: 200;
              }
              .story-title {
                font-size: 1rem;
                font-weight: 400;
                margin: 0;
                margin-bottom: 0.5em;
              }
              .story-title a {
                color: #333;
                text-decoration: none;
              }
              .story-title a:hover {
                text-decoration: underline;       
            }
            `}
        </style>
    </div>
);

export default StoryList;
